package CoreJava;

public class Break_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int age=2;
		
		switch (age) {
		
		case 1:
			System.out.println("you can crawl");
		break;
		
		case 2:
			System.out.println("you can talk");
		break;
		
		case 3:
			System.out.println("you can walk");
		break;
		
		case 4:
			System.out.println("you can run");
		break;
		
		case 5:
			System.out.println("you can sing");
		break;
		
//default:
		//System.out.println("i do not know our age");
		
		
		
		}
		
	if (age==1)	
	{
		System.out.println("you can go to school");
	
	}
	else
	{
		System.out.println("i want to stay home");
	}
	
		
		
		
		
	}

}
